var getEndOfMonth = function(year,month){ 
    var m = month.substr(0,1);
    if (m == "0"){
        month = month.substr(1,1);
    } 
    var endM = "30";
    var isLeapyear =false; 
    if (((year % 4)==0) &amp;&amp; ((year % 100)!=0) || ((year % 400)==0)) {
        isLeapyear = true;
    }
    if(month == 1) endM = 30;
    if(month == 2 &amp;&amp; isLeapyear == false) endM = 28;
    if(month == 2 &amp;&amp; isLeapyear == true) endM = 29; 
    if(month == 3) endM = 31;
    if(month == 4) endM = 30;
    if(month == 5) endM = 31;
    if(month == 6) endM = 30;
    if(month == 7) endM = 31;
    if(month == 8) endM = 31;
    if(month == 9) endM = 30;
    if(month == 10) endM = 31;
    if(month == 11) endM = 30;
    if(month == 12) endM = 31;     
    return endM;
} 

var getYear = function(){
    var date=new Date;  
    var year=date.getFullYear();
    return year; 
}


//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "  where ([sendNews].status='已读' or [sendNews].status='未读'or [sendNews].status='删除') ";
//es.logWarning(condition);  
if(e2!=0){                              
    condition =" where [sendNews].status ='未读'";
}  
if(e3!=0){
   condition =" where [sendNews].status ='已读'";
}
if(e4!=0){
   condition =" where [sendNews].status ='删除'";
}
if (e1!="") {
    e1 = e1.replace(/-/g,"/"); 
    e1 = e1.replace(/：/g,":");
    e1 = e1.replace(/(^\s*)|(\s*$)/g, ""); //去除字符串左右两边空格;
   // es.logWarning("messageManagement query")
    //es.logWarning("e1="+e1);
    var index = e1.indexOf("/"); 
    var start,end;
    var date= "";
    if(index != -1){ 
        var index1 = e1.indexOf(" ");
        if (index1 != -1 ){
            date = e1.substr(0,index1);
            end = date + " 23:59:59";
            start = e1;
        }else {
            var dArr = e1.split("/"); 
           // es.logWarning("dArr[].length ="+dArr.length);

            if (dArr.length == 3){ 
                start = e1;
                end = e1;

            }else if (dArr.length ==2){
                if (dArr[0] != "" &amp;&amp; dArr[1] != ""){
                    if(dArr[0] > 12){
                        if (dArr[0].length == 2){
                            e1 = "20"+ e1;
                            dArr[0] = "20"+dArr[0];
                        }
                        start= e1 + "/01";                          
                        end = e1 + "/" + getEndOfMonth(dArr[0],dArr[1]);
                    }else {
                        start = getYear() +"/"+ e1;
                        end = getYear() +"/"+ e1;
                    }
                }
            } 
            start += " 00:00:00";
            end += " 23:59:59";
        }           
        //es.logWarning("start="+start);
        //.logWarning("end="+end); 
        condition += " and [sendNews].regDatetime between '"+db.encode(start)+"' and '"+db.encode(end)+"'";
    }else {
        condition += " and concat(userInfo.nickName,userInfo.mobile,userInfo.idName,userInfo.userType,sendNews.status,sendNews.title,sendNews.newsContent) like '%" + db.encode(e1) + "%'";
    }
}   
var sql, sqlResult;    

//获取记录数量
sql = "select count(*) as c from sendNews left outer join userInfo on sendNews.userId = userInfo.id "+condition; 
//es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表 
sql =
    "select [sendNews].id,[sendNews].regDatetime,[sendNews].title,[sendNews].newsContent,[sendNews].status,[sendNews].userId , " +
    "[userInfo].nickName ,[userInfo].mobile,[userInfo].idName,[userInfo].userType " +
    "from [sendNews] LEFT OUTER JOIN userInfo ON [sendNews].userId = userInfo.id " + condition + " order by regDatetime desc ";

//es.logWarning(sql);    
var sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"id,nickName,idName,mobile,userType,regDatetime,status,title,newsContent", pageSize*(pageCurrent-1), pageSize);
