//处理mobileS ：去除左右两边的逗号 
while(1){
    var lastCode = mobileS.substr(mobileS.length-1,1);
    var fistCode = mobileS.substr(0,1);
    if (lastCode =="," || lastCode=="，"){
        mobileS = mobileS.substr(0,mobileS.length-1)
    }else if(fistCode =="," || fistCode == "，"){
        mobileS = mobileS.substring(1);
    }
    else {
        break;
    }
}
