taskSetItem.script2:
var newsTypecont = eio.readString8();
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var e1 = eio.readInt32(); //知识库
var e2 = eio.readInt32(); //常见问题
var e3 = eio.readInt32(); //系统公告
var e4 = eio.readInt32(); //其它
var sql, sqlResult; 

var condition = "where 1=1 "; 

if (e1!=0) {
    condition += " and newsType = '1'";
}
if (e2!=0) {
    condition += " and newsType = '2'";
}
if (e3!=0) {
    condition += " and newsType = '3'";
}
if (e4!=0) {
    condition += " and newsType = '0'";
}

if (newsTypecont != "") {
     condition +=" and concat(newsType,newsTypeStr,title) like '%" + db.encode(newsTypecont) + "%'";
}

//获取记录数量(用于分页)
sql = "select count(*) as c from [news] " + condition + ""; 
es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//查询news表数据(用于遍历)

sql = "select * from [news] " + condition + " order by regDatetime desc";
es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult, "id,newsType,newsTypeStr,title,lastModify,regDatetime", pageSize*(pageCurrent-1), pageSize);
//eio.appendDataSet(sqlResult, "id,newsType,newsTypeStr,title,lastModify,regDatetime", 0, 1000);
//es.logWarning("gggggggggggggggggggggggggggggggggg");

taskSetItem.script3:
g5.pageRecordCount = eio.readInt32();

g5.dataset1 = eio.readDataSet();

g5.pageCount = parseInt((g5.pageRecordCount-1)/g5.pageSize)+1;

ee.setTableData(ee.getControl("tablebody1"), g5.dataset1, 0);
ee.setText(ee.getControl("layoutxitem11"), g5.pageCurrent + "/" + g5.pageCount);

taskSetItem.script2:
var newsId = eio.readString8();
var sql = "select * from news where id = " + newsId;
var sqlResult = db.executeQuery("PJ", sql);
if (db.read(sqlResult)) {
    eio.appendString8(newsId);
    eio.appendString16(db.getString(sqlResult,"newsType"));
    eio.appendString16(db.getString(sqlResult,"title"));
    eio.appendString16(db.getString(sqlResult,"newsTypeStr"));
    eio.appendString16(db.getString(sqlResult,"text"));
} else {
    eio.appendString8("0");
    eio.appendString16("");
    eio.appendString16("");
    eio.appendString16("");
    eio.appendString16("");
}

