taskSetItem.script2:
var newsType = eio.readString8();
var condition;
if(newsType == 0){
    condition = "其它";
}
if(newsType == 1){
    condition = "知识库";
}
if(newsType == 2){
    condition = "常见问题";
}
if(newsType == 3){
    condition = "系统公告";
}

var sql = "insert into news (id ,newsType,title,newsTypeStr,text) values ( next value for SEQnews ,'" + newsType + "','文章标题','" + condition + "','内容')";
es.logWarning(sql);
db.executeNoneQuery("PJ", sql);

var newsId = eio.readString8();
var newsType = eio.readString16();
var title = eio.readString16();
var newsTypeStr = eio.readString16();
var text  = eio.readString16();

var sql = "update news set title = '" + db.encode(title) + "'" +
    ",newsType='" + db.encode(newsType) + "'" +
    ",newsTypeStr='" + db.encode(newsTypeStr) + "'" +
    ",text='" + db.encode(text) + "'" +
    " where id = " + newsId;

//var sql = "update [news] set title = 'aaa' where id = 211";
//var sql = "update userInfo set nickName='我是学员a' where id = 10 ";
//es.logWarning(sql);    
if (db.executeNoneQuery("PJ",sql)) {
    eio.appendString8("T");
} else {
    eio.appendString8("F");
}

