g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber<1) pageNumber = 1;
    if (pageNumber>g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent!=pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent>0) {
            query();
            search();
        }
    }
}

g5.refreshDetail = function(index) {
    ee.setText(ee.getControl("layoutxitem23"), ee.getText(ee.getControl("tablebody1").cols[0].rows[index]));
    ee.setEditText(ee.getControl("edit3"), ee.getText(ee.getControl("tablebody1").cols[1].rows[index]));
    ee.setEditText(ee.getControl("edit23"), ee.getText(ee.getControl("tablebody1").cols[2].rows[index]));
    ee.setEditText(ee.getControl("edit4"), ee.getText(ee.getControl("tablebody1").cols[3].rows[index]));
    ee.setEditText(ee.getControl("edit5"), ee.getText(ee.getControl("tablebody1").cols[4].rows[index]));
    ee.setEditText(ee.getControl("edit8"), ee.getText(ee.getControl("tablebody1").cols[5].rows[index]));
    ee.setEditText(ee.getControl("edit6"), ee.getText(ee.getControl("tablebody1").cols[6].rows[index]));
    ee.setEditText(ee.getControl("edit16"), ee.getText(ee.getControl("tablebody1").cols[7].rows[index]));
    ee.setEditText(ee.getControl("edit2"), ee.getText(ee.getControl("tablebody1").cols[8].rows[index]));
    ee.setEditText(ee.getControl("edit10"), ee.getText(ee.getControl("tablebody1").cols[9].rows[index]));
    ee.setEditText(ee.getControl("edit22"), ee.getText(ee.getControl("tablebody1").cols[10].rows[index]));
    ee.setEditText(ee.getControl("edit9"), ee.getText(ee.getControl("tablebody1").cols[11].rows[index]));
