control.onUpdate:
if(text.length > 0){
    if (text == '1new') {
        text = '待分配';
        ee.setText(sender, text);
        return;
    }

    if (text == '2met') {
        text = '已分配';
        ee.setText(sender, text);
        return;
    }

    if (text == '3prc') {
        text = '进行中';
        ee.setText(sender, text);
        return;
    }

    if (text == '4ass'){
        text = '待评价';
        ee.setText(sender, text);
    }

    if (text == "6del"){
        text = "待删除";

    } 
    if (text == "5cms"){
        text = "待转可提";

    }
}else{
    text = ''; 

}  
ee.setText(sender,text);

var res = text;

var arr = [];
arr = res.split(";");
//console.log(arr);
ee.setText(sender,text);

