var getEndOfMonth = function(year,month){ 
    var m = month.substr(0,1);
    if (m == "0"){
        month = month.substr(1,1);
    } 
    var endM = "30";
    var isLeapyear =false; 
    if (((year % 4)==0) &amp;&amp; ((year % 100)!=0) || ((year % 400)==0)) {
        isLeapyear = true;
    }
    if(month == 1) endM = 30;
    if(month == 2 &amp;&amp; isLeapyear == false) endM = 28;
    if(month == 2 &amp;&amp; isLeapyear == true) endM = 29; 
    if(month == 3) endM = 31;
    if(month == 4) endM = 30;
    if(month == 5) endM = 31;
    if(month == 6) endM = 30;
    if(month == 7) endM = 31;
    if(month == 8) endM = 31;
    if(month == 9) endM = 30;
    if(month == 10) endM = 31;
    if(month == 11) endM = 30;
    if(month == 12) endM = 31;     
    return endM;
} 

var getYear = function(){
    var date=new Date;  
    var year=date.getFullYear();
    return year; 
}

//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "where 1=1 and status != 'pgc' and status != 'pgc11'";
//es.logWarning(condition);
if (e0!="") {
    e0 = e0.replace(/-/g,"/"); 
    e0 = e0.replace(/：/g,":");
    e0 = e0.replace(/(^\s*)|(\s*$)/g, ""); //去除字符串左右两边空格;
    es.logWarning("order query")
    es.logWarning("e0="+e0);
    var index = e0.indexOf("/"); 
    var start,end;
    var date= "";
    if(index != -1){ 
        var index1 = e0.indexOf(" ");
        if (index1 != -1 ){
            date = e0.substr(0,index1);
            end = date + " 23:59:59";
            start = e0;
        }else {
            var dArr = e0.split("/"); 
            es.logWarning("dArr[].length ="+dArr.length);

            if (dArr.length == 3){ 
                start = e0;
                end = e0;

            }else if (dArr.length ==2){
                if (dArr[0] != "" &amp;&amp; dArr[1] != ""){
                    if(dArr[0] > 12){
                        if (dArr[0].length == 2){
                            e0 = "20"+ e0;
                            dArr[0] = "20"+dArr[0];
                        }
                        start= e0 + "/01";                          
                        end = e0 + "/" + getEndOfMonth(dArr[0],dArr[1]);
                    }else {
                        start = getYear() +"/"+ e0;
                        end = getYear() +"/"+ e0;
                    }
                }
            }
            start += " 00:00:00";
            end += " 23:59:59";
        }           
        es.logWarning("start="+start);
        es.logWarning("end="+end); 
        condition += " and (([task].time1 between '"+db.encode(start)+"' and '"+db.encode(end)+"') or ([task].time2 between '"+db.encode(start)+"' and '"+db.encode(end)+"'))";
    }else {     
        condition += " and concat([userInfo_1].nickName,[userInfo].nickName,[userInfo].idName,[userInfo_1].idName,[userInfo_1].mobile,[userInfo].mobile,[task].id,[task].address,[task].message,[task].provideCar,[task].carStall,[task].flags,[task].note) like '%" + db.encode(e0) + "%'";
    }
}
if (e1!=0) {
    condition += " and task.status = '1new'";
}
if (e2!=0) {
    condition += " and task.status = '2met'";
}
if (e3!=0) {
    condition += " and task.status = '3prc'";
}
var sql, sqlResult;

//获取记录数量
sql = "select count(*) as c from task LEFT OUTER JOIN [userInfo] AS [userInfo_1] ON [task].studentId = [userInfo_1].id " + 
      "LEFT OUTER JOIN [userInfo] ON [task].coachId = [userInfo].id " +condition;

es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

sql = "select [task].id,[task].status,[task].coachId,[task].studentId,[task].address,[task].time1,[task].time2,[task].classHour,[task].studentMobile, "+
      "[task].provideCar,cast([task].pay as decimal(18,2)) as pay,cast([task].payFree as decimal(18,2)) as payFree ,[task].message,[task].carStall, [task].flags,[task].note," + 
      "[userInfo_1].nickName,[userInfo_1].idName as studentidName," +
      "[userInfo].nickName AS teachName,[userInfo].idName as teachidName ,[userInfo].mobile AS teachMobile " +
      "from [task] LEFT OUTER JOIN [userInfo] AS [userInfo_1] ON [task].studentId = [userInfo_1].id " +
      "LEFT OUTER JOIN [userInfo] ON [task].coachId = [userInfo].id "+
      condition +
      "order by [task].status,id desc ";

es.logWarning(sql);    
var sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"id,nickName,studentidName,studentMobile,address,time1,time2,classHour,status,provideCar,carStall,teachName,teachidName,teachMobile,message,pay,payFree,flags,note", pageSize*(pageCurrent-1), pageSize);

