var getEndOfMonth = function(year,month){ 
    var m = month.substr(0,1);
    if (m == "0"){
        month = month.substr(1,1);
    } 
    var endM = "30";
    var isLeapyear =false; 
    if (((year % 4)==0) &amp;&amp; ((year % 100)!=0) || ((year % 400)==0)) {
        isLeapyear = true;
    }
    if(month == 1) endM = 30;
    if(month == 2 &amp;&amp; isLeapyear == false) endM = 28;
    if(month == 2 &amp;&amp; isLeapyear == true) endM = 29; 
    if(month == 3) endM = 31;
    if(month == 4) endM = 30;
    if(month == 5) endM = 31;
    if(month == 6) endM = 30;
    if(month == 7) endM = 31;
    if(month == 8) endM = 31;
    if(month == 9) endM = 30;
    if(month == 10) endM = 31;
    if(month == 11) endM = 30;
    if(month == 12) endM = 31;     
    return endM;
} 

var getYear = function(){
    var date=new Date;  
    var year=date.getFullYear();
    return year; 
}

//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "where 1=1 ";

if(e1!=1 &amp; e2!=1 &amp; e3!=1 &amp; e4!=1){
    condition +="and (logType = '订单完成' or logType = '订单取消'or logType= '订单取消1'or logType= '订单取消2'or logType='开单'or logType='接单'or logType='开始陪练'or logType='评价完成')";
}
if(e0!=""){ 
    e0 = e0.replace(/-/g,"/"); 
    e0 = e0.replace(/：/g,":");
    e0 = e0.replace(/(^\s*)|(\s*$)/g, ""); //去除字符串左右两边空格;
    es.logWarning("order search")
    es.logWarning("e0="+e0);
    var index = e0.indexOf("/"); 
    var start,end;
    var date= "";
    if(index != -1){ 
        var index1 = e0.indexOf(" ");
        if (index1 != -1 ){
            date = e0.substr(0,index1);
            end = date + " 23:59:59";
            start = e0;
        }else {
            var dArr = e0.split("/"); 
            es.logWarning("dArr[].length ="+dArr.length);

            if (dArr.length == 3){ 
                start = e0;
                end = e0;

            }else if (dArr.length ==2){
                if (dArr[0] != "" &amp;&amp; dArr[1] != ""){
                    if(dArr[0] > 12){
                        if (dArr[0].length == 2){
                            e0 = "20"+ e0;
                            dArr[0] = "20"+dArr[0];
                        }
                        start= e0 + "/01";                          
                        end = e0 + "/" + getEndOfMonth(dArr[0],dArr[1]);
                    }else {
                        start = getYear() +"/"+ e0;
                        end = getYear() +"/"+ e0;
                    }
                }
            }
            start += " 00:00:00";
            end += " 23:59:59";
        }           
        es.logWarning("start="+start);
        es.logWarning("end="+end); 
        condition += " and ((log.regDatetime between '"+db.encode(start)+"' and '"+db.encode(end)+"') or (dtParam1 between '"+db.encode(start)+"' and '"+db.encode(end)+"') or (dtParam2 between '"+db.encode(start)+"' and '"+db.encode(end)+"'))";

    }else {
        condition +=" and concat([userInfo].idName,[log].regDatetime,[userInfo].mobile,[userInfo].idName,[log].dtParam1,[log].dtParam2,[log].intParam1,u.idName,[log].intParam3,[log].intParam4,[log].intParam5 ,[log].logType,[log].strParam1,[log].strParam2,[log].strParam3,[log].note) like '%" + db.encode(e0) + "%'";
    }
}

if (e1!=0) {
    condition += " and logType = '订单完成'";
}
if (e2!=0) {
    condition += " and logType = '订单取消1'";
}
if (e3!=0) {
    condition += " and logType = '订单取消2'";
} 
if (e4!=0) {
    condition += " and logType = '评价完成'";
}

var sql, sqlResult;
//获取记录数量
sql = "select count(*) as c from log INNER JOIN [userInfo] ON userInfo.id = [log].userId  " +condition; 
es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

