var c1 = eio.readInt32();
var c2 = eio.readInt32();
var condition = " where 1=1 ";  


var getEndOfMonth = function(year,month){ 
    var m = month.substr(0,1);
    if (m == "0"){
        month = month.substr(1,1);
    } 
    var endM = "30";
    var isLeapyear =false; 
    if (((year % 4)==0) &amp;&amp; ((year % 100)!=0) || ((year % 400)==0)) {
        isLeapyear = true;
    }
    if(month == 1) endM = 30;
    if(month == 2 &amp;&amp; isLeapyear == false) endM = 28;
    if(month == 2 &amp;&amp; isLeapyear == true) endM = 29; 
    if(month == 3) endM = 31;
    if(month == 4) endM = 30;
    if(month == 5) endM = 31;
    if(month == 6) endM = 30;
    if(month == 7) endM = 31;
    if(month == 8) endM = 31;
    if(month == 9) endM = 30;
    if(month == 10) endM = 31;
    if(month == 11) endM = 30;
    if(month == 12) endM = 31;     
    return endM;
} 

var getYear = function(){
    var date=new Date;  
    var year=date.getFullYear();
    return year; 
}

if (e2!="") {
      // condition += " and concat(applyName,tel) like '%" + db.encode(e2) + "%'"; concat要求2到254个参数 1个不行 
    e2 = e2.replace(/-/g,"/"); 
    e2 = e2.replace(/：/g,":");
    e2 = e2.replace(/(^\s*)|(\s*$)/g, ""); //去除字符串左右两边空格;
    //es.logWarning("usercenter query")
    //es.logWarning("e2="+e2);
    var index = e2.indexOf("/"); 
    var start,end;
    var date= "";
    if(index != -1){ 
        var index1 = e2.indexOf(" ");
        if (index1 != -1 ){
            date = e2.substr(0,index1);
            end = date + " 23:59:59";
            start = e2;
        }else {
            var dArr = e2.split("/"); 
            //es.logWarning("dArr[].length ="+dArr.length);

            if (dArr.length == 3){ 
                start = e2;
                end = e2;

            }else if (dArr.length ==2){
                if (dArr[0] != "" &amp;&amp; dArr[1] != ""){
                    if(dArr[0] > 12){
                        if (dArr[0].length == 2){
                            e2 = "20"+ e2;
                            dArr[0] = "20"+dArr[0];
                        }
                        start= e2 + "/01";                          
                        end = e2 + "/" + getEndOfMonth(dArr[0],dArr[1]);
                    }else {
                        start = getYear() +"/"+ e2;
                        end = getYear() +"/"+ e2;
                    }
                }
            }
            start += " 00:00:00";
            end += " 23:59:59";
        }           
        //es.logWarning("start="+start);
        //es.logWarning("end="+end); 
        condition += " and applyTime between '"+db.encode(start)+"' and '"+db.encode(end)+"'";    
    }else {
        condition += " and concat(learnDrive.id,learnDrive.applyName,learnDrive.tel,learnDrive.schoolName,learnDrive.applyTime,learnDrive.status) like '%" + db.encode(e2) + "%'";
    }
} 
if(c1!=0){                              
    condition =" where status ='未付款'";
}  
if(c2!=0){
   condition =" where status ='已付款'";
}

var sql, sqlResult; 
sql="select count(*) as c from learnDrive"+condition ;
sqlResult = db.executeQuery("PJ",sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");
eio.appendInt32(count);
sql = "select * from learnDrive"+condition +" order by id desc";  
//es.logWarning(sql);
sqlResult = db.executeQuery("PJ",sql);
eio.appendDataSet(sqlResult,"id,applyName,tel,schoolName,con,applyTime,status,price1",pageSize*(pageCurrent-1),pageSize);

