g5.reason = ""; //修改金额理由
g5.balance = "";
g5.balanceFree = "";

g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber<1) pageNumber = 1;
    if (pageNumber>g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent!=pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent>0) {
            query();
        }
    }
}

g5.refreshDetail = function(index) {
    ee.setText(ee.getControl("layoutxitem23"), ee.getText(ee.getControl("tablebody1").cols[0].rows[index]));
    ee.setEditText(ee.getControl("edit9"), ee.getText(ee.getControl("tablebody1").cols[1].rows[index]));
    ee.setEditText(ee.getControl("edit10"), ee.getText(ee.getControl("tablebody1").cols[2].rows[index])); 
    ee.setEditText(ee.getControl("edit11"), ee.getText(ee.getControl("tablebody1").cols[3].rows[index]));
    ee.setEditText(ee.getControl("edit12"), ee.getText(ee.getControl("tablebody1").cols[4].rows[index]));
    ee.setEditText(ee.getControl("edit13"), ee.getText(ee.getControl("tablebody1").cols[5].rows[index]));
    ee.setEditText(ee.getControl("edit14"), ee.getText(ee.getControl("tablebody1").cols[6].rows[index]));
    ee.setEditText(ee.getControl("edit15"), ee.getText(ee.getControl("tablebody1").cols[7].rows[index]));
    ee.setEditText(ee.getControl("edit16"), ee.getText(ee.getControl("tablebody1").cols[8].rows[index]));
    ee.setEditText(ee.getControl("edit17"), ee.getText(ee.getControl("tablebody1").cols[9].rows[index]));
    ee.setEditText(ee.getControl("edit18"), ee.getText(ee.getControl("tablebody1").cols[10].rows[index]));
    ee.setEditText(ee.getControl("edit19"), ee.getText(ee.getControl("tablebody1").cols[11].rows[index]));
    ee.setEditText(ee.getControl("edit2"), ee.getText(ee.getControl("tablebody1").cols[12].rows[index]));
    ee.setEditText(ee.getControl("edit4"), ee.getText(ee.getControl("tablebody1").cols[13].rows[index]));
    ee.setEditText(ee.getControl("edit5"), ee.getText(ee.getControl("tablebody1").cols[14].rows[index]));
    ee.setEditText(ee.getControl("edit6"), ee.getText(ee.getControl("tablebody1").cols[15].rows[index]));
    ee.setEditText(ee.getControl("edit3"), ee.getText(ee.getControl("tablebody1").cols[16].rows[index]));  
    ee.setEditText(ee.getControl("edit7"), ee.getText(ee.getControl("tablebody1").cols[17].rows[index])); 
    ee.setEditText(ee.getControl("edit30"), ee.getText(ee.getControl("tablebody1").cols[18].rows[index]));
    ee.setEditText(ee.getControl("edit32"), ee.getText(ee.getControl("tablebody1").cols[19].rows[index])); 
    ee.setEditText(ee.getControl("edit34"), ee.getText(ee.getControl("tablebody1").cols[22].rows[index])); 
    ee.setEditText(ee.getControl("edit37"), ee.getText(ee.getControl("tablebody1").cols[24].rows[index]));
    g5.balance = ee.getText(ee.getControl("tablebody1").cols[4].rows[index]);
    g5.balanceFree = ee.getText(ee.getControl("tablebody1").cols[5].rows[index]);

//    ee.setEditText(ee.getControl("edit3"), ee.getText(ee.getControl("tablebody1").cols[17].rows[index]));

}

