var Mobile = ee.getTableCellText(ee.getControl("tablebody1"),2,this.rowIndex);
var stuAddress = ee.getTableCellText(ee.getControl("tablebody1"),6,this.rowIndex);
//alert(text)     
if(text == "学员"){
    ee.setVisible(ee.getControl("panel22"),true);
    ee.setVisible(ee.getControl("panel48"),true);
    ee.setEditText(ee.getControl("edit8"),Mobile);
    ee.setEditText(ee.getControl("edit22"),stuAddress);
    //填数据

    return;
}else if (text == "教练"){

    ee.setVisible(ee.getControl("panel39"),true);
    ee.setVisible(ee.getControl("panel48"),true);
    ee.setEditText(ee.getControl("edit29"),Mobile);
    var coaName = ee.getTableCellText(ee.getControl("tablebody1"),3,this.rowIndex);
    ee.setText(ee.getControl("panel45"),coaName);
    return; 

}else {
    alert("出错");
}

control.onUpdate:
if(text.length > 0){
    if (text == 'VNN') {
        text = '已审核';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'VCN') {
        text = '认证申请';
        ee.setText(sender, text);
        return;
    }

    if (text == 'VVN') {
        text = '已认证';
        ee.setText(sender, text);
        return;
    }
    if (text == 'CNN') {
        text = '审核申请';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'VVV') {
        text = '已测试';
        ee.setText(sender, text);
        return;
    }
    if (text == 'NNN') {
        text = '';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'PNN') {
        text = '已培训';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'ST') {
        text = '已签约';
        ee.setText(sender, text);
        return;
    }

    ee.setText(sender,text);
}else {
    text = ''; 
    ee.setText(sender,text);
}

if (text.substr(0,1) == "/"){
    area = text.substring(1);
}else {
    area = text;
}


ee.setText(sender,area);

if(text.length > 0){
    if (text == 'T') {
        text = '有效';
        ee.setText(sender, text);
        return;
    }

    if (text == 'F') {
        text = '删除';
        ee.setText(sender, text);
        return;
    }
}else{
    text = ''; 
    ee.setText(sender,text);
}

control.onClick:
g5.refreshDetail(this.rowIndex); 
var row = this.rowIndex;  
var id = ee.getText(ee.getControl("tablebody1").cols[0].rows[row]);
//alert(row+"  "+id);; 
g5.getIdForBake = id;     
ee.setEditText(ee.getControl("edit33"),ee.getText(this));
ee.setVisible(ee.getControl("panel61"),true); 
ee.setVisible(ee.getControl("panel48"),true);

control.onUpdate:
if (text != ""){
    ee.setTextColor(sender,"red");
}   
var tmp = "";       
var tmp2 = "";
var index = text.indexOf("福建抽奖活动");  
var index1 = text.indexOf(" ");   
if(index1 == -1){
    text += " ";
    index1 = text.indexOf(" "); 
}
if (index < index1 &amp;&amp; index != -1){
    tmp = text.substr(index,index1 - index);
    tmp2 = text.substring(index1);
    //console.log("tmp="+tmp);
    if (tmp != ""){
        tmp = tmp.replace("活动_","*");
        tmp = tmp.replace("_","*");   
        if(index != -1 &amp;&amp; tmp.substring(tmp.length - 2)!="免费"){
            tmp += "元";    
        }
    }
    text = tmp + tmp2;           
}

ee.setText(sender, text);

control.onClick:
var content = ee.getText(this);  
//alert(content);  
var f = content;
if (content == "" || content == "N"){
    ee.setImage(this.eeChildren[0],"/App1PJ/regCoaches/gou2.png","trim","","");  
    f = "Y";
} else {
    ee.setImage(this.eeChildren[0],"/App1PJ/regCoaches/gou1.png","trim","","");
    f = "";
}

if (f != content){
    g5.id = ee.getText(ee.getControl("tablebody1").cols[0].rows[this.rowIndex]);
    g5.f = f; 
    console.log("id="+g5.id);
    console.log("f="+g5.f);
    changeGiftFlag();
    ee.setText(this,f);    
}

control.onUpdate:
if(text.length > 0){     
    var userType = ee.getTableCellText(ee.getControl("tablebody1"),7,sender.rowIndex); 
  //  console.log(userType);
    if (text == '0'&amp;&amp; userType=='教练') {
        text = '兼职';
        ee.setText(sender, text);
        return;
    } 
    if (text == '1'&amp;&amp; userType=='教练') {
        text = '专职';
        ee.setText(sender, text);
        return;
    }
    if(userType!=='教练'){
       text='';
       ee.setText(sender, text);
       return;
    }

  //  ee.setText(sender,text);
}else {
    text = ''; 
    ee.setText(sender,text);
}

