g5.getCount = function(v){
    var tmp =v; 
    var count=0;
    while(1){
        var index = tmp.indexOf("/"); 
        if (index == -1){
            count =0;
            break;
        }
        count++;
        tmp = tmp.substring(index+1);
        if (tmp ==""){
            break;
        }    
    }
    return count;     
} 
