            "'"+db.encode(Id)+"','后台更改金额','','"+money+"','"+moneyFree+"','"+balance+"','"+balanceFree+"','"+db.encode(reason)+"',"+
           "'"+db.encode(loginName)+ "')";
   // es.logWarning(sql);                                 
    db.executeNoneQuery("PJ", sql);     
    balance = balance * 0.01; 
    balanceFree = balanceFree * 0.01;   

