}

control.onUpdate:
if(text.length > 0){
    if (text == 'VNN') {
        text = '已审核';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'VCN') {
        text = '认证申请';
        ee.setText(sender, text);
        return;
    }

    if (text == 'VVN') {
        text = '已认证';
        ee.setText(sender, text);
        return;
    }
    if (text == 'CNN') {
        text = '审核申请';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'VVV') {
        text = '已测试';
        ee.setText(sender, text);
        return;
    }
    if (text == 'NNN') {
        text = '';
        ee.setText(sender, text);
        return;
    } 
    if (text == 'PNN') {
        text = '已培训';
        ee.setText(sender, text);
        return;
    }

    ee.setText(sender,text);
}else {
    text = ''; 
    ee.setText(sender,text);
}

if (text.substr(0,1) == "/"){
    area = text.substring(1);
}else {
    area = text;
}


ee.setText(sender,area);

if(text.length > 0){
    if (text == 'T') {
        text = '有效';
        ee.setText(sender, text);
        return;
    }

    if (text == 'F') {
        text = '删除';
        ee.setText(sender, text);
        return;
    }
}else{
    text = ''; 
    ee.setText(sender,text);
}

