if (condition != "") {
    condition = "and newsType='" + condition + "'"
}
var sql = "select * from news where siteId=3 " + condition + " order by newsType, regdatetime desc";
var sqlResult = db.executeQuery("EBWeb", sql);
eio.appendDataSet(sqlResult, "id,newsType,title,subTitle,regdatetime", 0, 100);

var newsType = eio.readString8();
var sql = "insert into news (siteId,newsType,title,subTitle,data) values (3,'" + newsType + "','新闻标题','副标题','内容')";
db.executeNoneQuery("EBWeb", sql);

var newsId = eio.readString8();
var sql = "select * from news where id = " + newsId;
var sqlResult = db.executeQuery("EBWeb", sql);
if (db.read(sqlResult)) {
    eio.appendString8(newsId);
    eio.appendString16(db.getString(sqlResult,"newsType"));
    eio.appendString16(db.getString(sqlResult,"title"));
    eio.appendString16(db.getString(sqlResult,"subTitle"));
    eio.appendString16(db.getString(sqlResult,"data"));
} else {
    eio.appendString8("0");
    eio.appendString16("");
    eio.appendString16("");
    eio.appendString16("");
    eio.appendString16("");
}

