taskSetItem.script2:
var newsId = eio.readString8();
var newsType = eio.readString16();
var title  = eio.readString16();
var subTitle  = eio.readString16();
var data  = eio.readString16();
var sql = "update news set title = '" + db.encode(title) + "'" +
    ",newsType='" + db.encode(newsType) + "'" +
    ",subTitle='" + db.encode(subTitle) + "'" +
    ",data='" + db.encode(data) + "'" +
    " where id = " + newsId;
if (db.executeNoneQuery("EBWeb",sql)) {
    eio.appendString8("T");
} else {
    eio.appendString8("F");
}

