control.onControlLoad:
g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber<1) pageNumber = 1;
    if (pageNumber>g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent!=pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent>0) {
            if(checkIndex==0){  
                getPayAll();  
            }   
            else {
                getPay();
            }
        }
    }
}

getPayAll();  
var checkIndex=0;
var idArr=new Array();
//g5.flagNum=1;

g5.refreshDetail = function(index) { 

    var panel = ee.getControl("tablebody2").cols[0].rows[index]; 
    //var cellText=ee.getTableCellText(ee.getControl("tablebody1"), 0, this.rowIndex);  
    if(panel.id.length<=0){
        return;
    }     

    for(var i=0;i<15;i++){   
        for(var k=0;k<5;k++){
            var panelOther = ee.getControl("tablebody2").cols[k].rows[i]; 
            panelOther.style.background="none";
        }    
    }  
    for(var j=0;j<5;j++){
        var panelChange=ee.getControl("tablebody2").cols[j].rows[index];  
        panelChange.style.background="#F8F8F8"; 
    }


    g5.userID=panel.uid;
    idArr[0]=g5.user; 

    g5.checkId=g5.userID     
    checkQuery();
    ee.getControl("panel22").onclick=function(){

        if(idArr[checkIndex]==g5.userID){
            alert("id相同") 
            return;
        }  

        //g5.checkId=g5.userID  
        //checkQuery();
        //alert(g5.flagNum)
        if(g5.flagNum==1){ //无下线记录 下面不执行 
            alert("无下级")           
            return;
        }  
        g5.pageCurrent=1;
        getPay(); 
        checkIndex+=1; 
        idArr[checkIndex]=g5.userID;     
         //alert("checkIndex:"+checkIndex) 
        //ee.setText(ee.getControl("layoutxitem45"), g5.pageCurrent + "/" + g5.pageCount);   

    }
   ee.getControl("panel21").onclick=function(){

       //alert("checkIndex:"+checkIndex)    
        if(checkIndex==0){
            alert("已返回到最顶层");
            return;
        }  

        checkIndex-=1;       
        //alert("checkIndex:"+checkIndex) 
        g5.checkId=idArr[checkIndex]  
        //alert(g5.checkId)
        g5.pageCurrent = 1; 
        getPay(); 

        //ee.setText(ee.getControl("layoutxitem45"), g5.pageCurrent + "/" + g5.pageCount);
    }



}

control.onUpdate:
if(text.length > 0){
  var data = text.split("`");

   //ee.setText(sender, data[0]);
   sender.id = data[1]; 
   sender.uid = data[0];     
   ee.setText(sender,data[1]); 
   g5.flagFace=true;
}else {
    text = ''; 
    ee.setText(sender,text);  
    g5.flagFace=false;
}

if(text.length>0){
    ee.setImage(sender, "url:/userRes/"+text,"fill");
}
else { 
    text="";
    ee.setText(sender,text); 
    if(g5.flagFace==false){
        //ee.setImage(sender, "url:/userRes/"+text,"fill");  
        ee.setImage(sender, "url:Res/empty.png","fill");
    }  
    else { 
        ee.setImage(sender, "url:/userRes/face.png","fill");
    }
}

if (text.length<6) {
    ee.setText(sender,"");
    return;
}
var newText = text.substring(0,4) + "****" + text.substring(text.length-4);
ee.setText(sender,newText);

if(text.length>0){
    var textShow=text.replace("/"+g5.user+"/","*");//因为不知道id的长度所以统一替换为* 
    var index=textShow.indexOf("*");
    textShow=textShow.substr(index+1,textShow.length); 
    var textArr=textShow.split("/");//级数最后是*号代表1个 split/会多出一个 长度算法是*号的1个加上split出的textArr.length减1 所以就==textArr.length
    ee.setText(sender,textArr.length);
    //alert(textShow);
}              
else {
    text="";
    ee.setText(sender,text);
}

taskSetItem.script2:
var token = eio.readString8();
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();



var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}   
var userId = db.getString(sqlResult, "id");

//获取记录数量
sql = "select count(*) as c from userTree left outer join log on dbo.userTree.userId = dbo.[log].userId "+
      "LEFT OUTER JOIN dbo.userInfo ON dbo.userTree.userId = dbo.userInfo.id "+
      "where path like '%"+userId+"%' and "+
      "logType='财充值'";   
sqlResult = db.executeQuery("PJ", sql);   
es.logWarning(sql); 
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表   ISNULL(userType,'')+'`'+ISNULL(idNumber,'')
sql=  "SELECT ISNULL(cast(dbo.userTree.userId as varchar),'')+'`'+ISNULL(cast(dbo.log.id as varchar),'') as data,"+ 
      "dbo.userTree.parentUserId, dbo.userTree.path,"+ 
      "dbo.userTree.regDatetime, dbo.userTree.id, "+
      "dbo.[log].logType,dbo.userInfo.mobile,dbo.userInfo.photo1,dbo.[log].intParam2 "+
      "FROM dbo.userTree LEFT OUTER JOIN "+
      "dbo.[log] ON dbo.userTree.userId = dbo.[log].userId LEFT OUTER JOIN "+
      "dbo.userInfo ON dbo.userTree.userId = dbo.userInfo.id "+
      "where path like '%" + userId + "%' and logType='财充值'";
sqlResult = db.executeQuery("PJ", sql); 
es.logWarning(sql); 
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"data,photo1,mobile,path,intParam2", pageSize*(pageCurrent-1), pageSize); 
eio.appendString8(userId);
es.logWarning(count); 
//es.logWarning(pageCurrent); 
es.logWarning("success");

taskSetItem.script3:
