control.onClick:
g5.nickName = ee.getText(this);
if (g5.nickName.length==0) return;
g5.refreshDetail(this.rowIndex); 
g5.pageCurrent = 1;
query();

control.onUpdate:
if(text.length > 0){
    if (text == 'T') {
        text = '有效';
        ee.setText(sender, text);
        return;
    }

    if (text == 'F') {
        text = '删除';
        ee.setText(sender, text);
        return;
    }
}else{
    text = ''; 
    ee.setText(sender,text);
}

