taskSetItem.script1:
var e1 = ee.getEditText(ee.getControl("edit9"));    //起始时间

//发送
eio.appendInt32(g5.pageSize);
eio.appendInt32(g5.pageCurrent);
eio.appendString16(e1);
eio.appendString16(g5.nickName);          //用户昵称

taskSetItem.script2:
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var e1 = eio.readString16();
var nickName = eio.readString16();    //用户昵称

var sql, sqlResult;
//产生条件语句 where accountName='" + db.encode(session.get("username")) + "'
var condition = "where 1=1";
if (e1!="") {
    condition += " and concat([userInfo].nickName,[userInfo].idName,[userInfo].mobile,[bankCard].cardUserName,[bankCard].cardNumber,bankName) like '%" + db.encode(e1) + "%'";
}

if(nickName!=""){
    //condition +=" and bankCard.userId = " + userId ;
    //获取用ID
    sql  = "select id as c from userInfo where nickName='" + nickName + "'";
    es.logWarning(sql);
    sqlResult= db.executeQuery("PJ", sql);
    db.read(sqlResult);
    var userId = db.getString(sqlResult, "c");   
    es.logWarning(userId); 
    condition +=" and bankCard.userId = " + userId ;
}

//获取记录数量
sql = "select count(*) as c from bankCard " + condition;
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表
sql =
    "SELECT *, " +
    "[userInfo].nickName,[userInfo].idName,[userInfo].idNumber,[userInfo].mobile " +
    " from bankCard LEFT OUTER JOIN [userInfo] ON [userInfo].id = [bankCard].userId " +
    condition +" order by  [bankCard].regDatetime  desc";  
es.logWarning(sql);    
sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult, "id,regDatetime,nickName,idName,mobile,cardUserName,cardNumber,bankName,status", pageSize*(pageCurrent-1), pageSize);

taskSetItem.script3:
g5.pageRecordCount = eio.readInt32();
g5.dataset1 = eio.readDataSet();
g5.pageCount = parseInt((g5.pageRecordCount-1)/g5.pageSize)+1;
ee.setTableData(ee.getControl("tablebody2"), g5.dataset1, 0);
ee.setText(ee.getControl("layoutxitem45"), g5.pageCurrent + "/" + g5.pageCount);

