taskSetItem.script2:
var mobile = eio.readString16();  //账户手机
var cardUserName = eio.readString16();  //持卡人姓名
var cardNumber = eio.readString16();  //银行卡卡号
var bankName = eio.readString16();  //开户行

var sql, sqlResult;

sql = "select id as c from userInfo where userType = '教练' and  mobile= '" + db.encode(mobile) + "'";
es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var userId = db.getString(sqlResult, "c");
es.logWarning(userId);

if(!userId){  
    eio.appendString8("F");
    return;
}
var sql = "insert into bankCard (userId,cardUserName,cardNumber,bankName) values (  " +
          " '" + userId +"' ,'" + cardUserName + "','" + cardNumber + "','" + bankName + "')";
es.logWarning(sql);
db.executeNoneQuery("PJ", sql);
eio.appendString8("T");

taskSetItem.script3:
