taskSetItem.script2:
var Id = eio.readString16(); //ID
var status = eio.readString16(); //银行卡状态

var sql, sqlResult;


if(status == "有效"){
    status = "F";
}
if(status == "删除"){
    status = "T";
}                      
var sql, sqlResult;
sql = 
        "update bankCard set " +
        "status='" + db.encode(status) + "'" +
        "where id=" + Id;
es.logWarning(sql);                                 
db.executeNoneQuery("PJ", sql);
eio.appendString8("T");
es.logWarning("success");

