g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber<1) pageNumber = 1;
    if (pageNumber>g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent!=pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent>0) {
            query();
        }
    }
}

g5.bodyClick = function(x,y) {
//alert("111111"); 
    var cell = ee.getTableCell(ee.getControl("tablebody2"), 0,y);
    if (ee.getText(cell).length<=0) return;
    g5.deleteCardId = ee.getTableCellText(ee.getControl("tablebody2"), 0, y);
//    alert(ee.getTableCell(ee.getControl("tablebody2"), 0, y)); 
    ee.setText(ee.getControl("panel11"), "持卡人姓名：" + ee.getText(ee.getTableCell(ee.getControl("tablebody2"), 1, y)));
    ee.setText(ee.getControl("panel25"), "银行卡卡号：" + ee.getText(ee.getTableCell(ee.getControl("tablebody2"), 2, y))); 
    ee.setText(ee.getControl("panel26"), "开户行：" + ee.getText(ee.getTableCell(ee.getControl("tablebody2"), 3, y)));
 //   ee.setText(ee.getControl("panel27"), "添加时间：" + ee.getText(ee.getTableCell(ee.getControl("tablebody1"), 3, y)));
    //alert(ee.getTableCell(ee.getControl("tablebody1"), 1, y).cardId);
    //g5.deleteCardId = parseInt(ee.getTableCell(ee.getControl("tablebody1"), 1, y).cardId);
    ee.showAni(ee.getControl("panel6"));  
 //   alert("222");  

}
query();

control.onUpdate:
if (text.length<6) {
    ee.setText(sender,"");
    return;
}
var newText = text.substring(0,4) + "****" + text.substring(text.length-4);
ee.setText(sender,newText);

if(text.length>0){
    var textShow=text.replace("/675/","*");//因为不知道id的长度所以统一替换为* 
    var index=textShow.indexOf("*");
    textShow=textShow.substr(index+1,textShow.length); 
    var textArr=textShow.split("/");//级数最后是*号代表1个 split/会多出一个 长度算法是*号的1个加上split出的textArr.length减1 所以就==textArr.length
    ee.setText(sender,textArr.length);
    //alert(textShow);
}              
else {
    text="";
    ee.setText(sender,text);
}

taskSetItem.script2:
var token = eio.readString8();
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();

var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
} 
var userId = db.getString(sqlResult, "id");

//获取记录数量
sql = "select count(*) as c from userTree where path like '%" + userId + "%'";
es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

sql=  "SELECT dbo.userTree.userId, dbo.userTree.parentUserId, dbo.userTree.path,"+ 
      "dbo.userTree.regDatetime, dbo.userTree.id, "+
      "dbo.[log].logType,dbo.userInfo.mobile,dbo.userInfo.photo1,dbo.[log].intParam2 "+
      "FROM dbo.userTree LEFT OUTER JOIN "+
      "dbo.[log] ON dbo.userTree.userId = dbo.[log].userId LEFT OUTER JOIN "+
      "dbo.userInfo ON dbo.userTree.userId = dbo.userInfo.id "+
      "where path like '%" + userId + "%' and logType='财充值'";

es.logWarning(sql);
sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult, "log.id,photo,mobile,intParam2,path", pageSize*(pageCurrent-1), pageSize);
eio.appendString8("T");
es.logWarning("success");

taskSetItem.script3:
