control.onUpdate:
if(text.length > 0){
  var data = text.split("`");

   //ee.setText(sender, data[0]);
   sender.type = data[0];   
   if(data[1].length>0){
       sender.idNumber = "****"+data[1].substring(data[1].length-6,data[1].length); 
   } 
   else {
       sender.idNumber="";
   }  
    if(data[2].length>0){
       sender.idName = "*"+data[2].substring(1,data[1].length); 
   } 
   else {
       sender.idName="";
   }
   sender.autoStatus=data[3];
   sender.userId=data[4];
   ee.setText(sender,data[0]);
}else {
    text = ''; 
    ee.setText(sender,text);
}

if(text.length>0){
ee.setImage(sender, "url:/userRes/"+text,"fill");
}
else {
    text="";
    ee.setText(sender,text);  
    ee.setImage(sender, "url:/userRes/"+text,"fill");
}

