taskSetItem.script2:
var token = eio.readString8();
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();


var sql, sqlResult;

var sql,sqlResult;
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    eio.appendString8("token失效");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}   
var userId = db.getString(sqlResult, "id");

//获取记录数量
sql = "select count(*) as c from userTree where parentUserId="+userId;
sqlResult = db.executeQuery("PJ", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表
sql="select ISNULL(userType,'')+'`'+ISNULL(idNumber,'')+'`'+ISNULL(idName,'')+'`'+ISNULL(autoStatus,'')+'`'+ISNULL(cast(id as varchar),'') as data,"+
"regDatetime,lastAccessTime,mobile,nickName,photo1"+
" where id in(select userId from userTree where parentUserId="+userId+")"; 
es.logWarning(sql);    
sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult,"data,regDatetime,lastAccessTime,mobile,nickName,photo1", pageSize*(pageCurrent-1), pageSize); 
eio.appendString8(userId);

