taskSetItem.script2:
var Id = eio.readInt32();                    //ID
var nickName = eio.readString16();           //用户昵称
var mobile = eio.readString16();             //用户手机
var idName = eio.readString16();             //身份证姓名
var balance = eio.readInt32();               //金额
var balanceFree = eio.readInt32();           //赠送金额
var address = eio.readString16();            //地址
var userType = eio.readString16();           //用户类型
var authStatus = eio.readString16();         //认证状态
var applyStatus= eio.readString16();         //审核状态
var carId = eio.readString16();              //车牌号码
var carType = eio.readString16();           //汽车型号
var licenseStart = eio.readInt32();         //初次领证年份
var idNumber = eio.readString16();          //身份证号码
var licenseNumber = eio.readString16();   //驾照号码
var serviceArea = eio.readString16();     //陪练区域
es.logWarning(applyStatus);
if(authStatus == "未认证"){
    authStatus = "N";
}
if(authStatus == "已认证"){
    authStatus = "V";
}
if(applyStatus == "未审核"){
    applyStatus = "F";
}
if(applyStatus == "审核通过"){
    applyStatus = "T";
}                      
var sql, sqlResult;
sql = 
        "update userInfo set " +
        "nickName='" + db.encode(nickName) + "'" +
        ",mobile='" + db.encode(mobile) + "'" +  
        ",idName='" + db.encode(idName) + "'" +
        ",balance=" + balance  +
        ",balanceFree=" + balanceFree +
        ",address ='" + db.encode(address) + "'" +
        ",userType='" + db.encode(userType) + "'" +
        ",authStatus='" + db.encode(authStatus) + "'" +
        ",applyStatus='" + db.encode(applyStatus) + "'" +
        ",carId='" + db.encode(carId) + "'" + 
        ",carType='" + db.encode(carType) + "'"+ 
        ",licenseStart=" + licenseStart + 
        ",idNumber='" + db.encode(idNumber) + "'" +
        ",licenseNumber='" + db.encode(licenseNumber) + "'" +
        ",serviceArea='" + db.encode(serviceArea) + "'" +
        " where id=" + Id;
es.logWarning(sql);                                 
db.executeNoneQuery("PJ", sql);
eio.appendString8("T");
es.logWarning("success");

