    var textShow=text.replace("/"+g5.user+"/","*");//因为不知道id的长度所以统一替换为* 
    var index=textShow.indexOf("*");
    textShow=textShow.substr(index+1,textShow.length); 
    var textArr=textShow.split("/");//级数最后是*号代表1个 split/会多出一个 长度算法是*号的1个加上split出的textArr.length减1 所以就==textArr.length
    ee.setText(sender,textArr.length);
    //alert(textShow);
}              
else {
    text="";
    ee.setText(sender,text);
}

