    "LEFT OUTER JOIN userTree on userTree.userId=userInfo.id "+
    "WHERE (dbo.[log].logType = '财开单' OR dbo.[log].logType = '财订单取消1'OR "+
    "dbo.[log].logType = '财提成' OR dbo.[log].logType is null) AND (dbo.userTree.[path] LIKE '%/"+userId+"/%') "+
