sql="SELECT userInfo.userType,userInfo.regDatetime,userInfo.photo1,"+
    "dbo.userInfo.id, dbo.userInfo.lastAccessTime,dbo.userInfo.mobile, dbo.userInfo.nickName, userTree.path, "+
    "sum(CASE logType WHEN '财开单' THEN dbo.[log].intParam2  ELSE 0 END) +  "+
    "sum(CASE logType WHEN '财订单取消1' THEN dbo.[log].intParam2   ELSE 0 END) as xiaofei, "+
    "sum(CASE logType WHEN '财提成' THEN dbo.[log].intParam2 ELSE 0 END) AS ticheng "+
    "FROM dbo.[log] INNER JOIN dbo.userInfo ON dbo.[log].userId = dbo.userInfo.id "+
    "Inner join userTree on userTree.userId=userInfo.id "+
    "WHERE (dbo.[log].logType = '财开单' OR dbo.[log].logType = '财订单取消1' OR "+
    "dbo.[log].logType = '财提成') AND (dbo.userInfo.id IN(SELECT userId FROM dbo.userTree "+
    "WHERE (path LIKE '%"+checkId+"%')))  "+
    "group BY dbo.userInfo.id,dbo.userInfo.lastAccessTime,dbo.userInfo.mobile,dbo.userInfo.nickName,userTree.path, "+
    "dbo.userInfo.userType,dbo.userInfo.regDatetime,dbo.userInfo.photo1";


es.logWarning(sql);    
sqlResult = db.executeQuery("PJ", sql);
eio.appendInt32(count);
