taskSetItem.script2:
var name = eio.readString8();
var trim = function (str){ //删除左右两端的空格 
    return str.replace(/(^\s*)|(\s*$)/g, ""); 
}
name = trim(name);
while (true) {
    if (name.length<2) {
        eio.appendString8("F");
        eio.appendString8("名字必须大于等于2个字符的长度");
        break;
    } 
    if (name.length>20) {
        eio.appendString8("F");
        eio.appendString8("名字必须小于等于20个字符的长度");
        break;
    }
    var sql = "select * from userInfo where nickName='" + db.encode(name) + "' ";
    var sqlResult = db.executeQuery("PJ", sql);
    if (sqlResult == null) {
        eio.appendString8("F");
        eio.appendString8("系统繁忙请稍后再试");
        break;
    }
    if (db.read(sqlResult)) {
        eio.appendString8("F");
        eio.appendString8("昵称已经存在，请换个名字,再点击“确认修改”");
        break;
    }
    eio.appendString8("T");
    break;
}

