taskSetItem.script2:
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var e1 = eio.readString8();
//产生条件语句
var condition = "where accountName='" + db.encode(session.get("username")) + "'";
if (e1.length>0) {
    condition += " and (empAccountName like '%" + db.encode(e1) + "%'";
    condition += " or cnName like '%" + db.encode(e1) + "%'";
    condition += " or note like '%" + db.encode(e1) + "%')";
}
var sql, sqlResult;

//获取记录数量
sql = "select count(*) as c from MfEmp " + condition;
sqlResult = db.executeQuery("QR", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表
sql =
    "SELECT id, empAccountName, cnName, left(convert(varchar, regdatetime, 120), 16) as regdatetime, note from MfEmp " +
    condition + " order by regDatetime desc";
var sqlResult = db.executeQuery("QR", sql);
eio.appendInt32(count);
eio.appendDataSet(sqlResult, "id,empAccountName,cnName,regdatetime,note", pageSize*(pageCurrent-1), pageSize);

taskSetItem.script3:
g5.pageRecordCount = eio.readInt32();
g5.dataset1 = eio.readDataSet();
g5.pageCount = parseInt((g5.pageRecordCount-1)/g5.pageSize)+1;
ee.setTableData(ee.getControl("tablebody1"), g5.dataset1, 0);
ee.setText(ee.getControl("layoutxitem11"), g5.pageCurrent + "/" + g5.pageCount);

