taskSetItem.script2:
var username = eio.readString16();
var password = eio.readString16();
var cnName = eio.readString16();
var note = eio.readString16();

var sql, sqlResult;

//检测用户名是否存在
sql = 
    "select * from mfEmp where accountName='" + db.encode(session.get("username")) + "' "+
    " and empAccountName='" + db.encode(username) + "'";
sqlResult = db.executeQuery("QR", sql);
if (db.read(sqlResult)) {
    eio.appendString8("F");
    eio.appendString8("账号已经存在");
} else {
    sql = 
        "insert into mfEmp (accountName,empAccountName,password,cnName,note) values (" +
        "'" + db.encode(session.get("username")) + "'," +
        "'" + db.encode(username) + "'," +
        "'" + db.encode(password) + "'," +
        "'" + db.encode(cnName) + "'," +
        "'" + db.encode(note) + "')";
    db.executeNoneQuery("QR", sql);
    eio.appendString8("T");
}

