taskSetItem.script1:
var e1 = ee.getEditText(ee.getControl("edit1"));
var e2 = ee.getEditText(ee.getControl("edit2"));
var e3 = ee.getEditText(ee.getControl("edit3"));
var e4 = ee.getEditText(ee.getControl("edit4"));
//客户端验证
if (e1 == "") {
    e1 = 0;
} else {
    e1 = parseInt(e1);
    if (!(e1>0)) {
        alert("批号只能是大于零的整数");
        return;
    }
}
if (e3 == "") {
    e3 = 0;
} else {
    e3 = parseInt(e3);
    if (!(e3>0)) {
        alert("起始序号只能是大于零的整数");
        return;
    }
}
if (e4 == "") {
    e4 = 0;
} else {
    e4 = parseInt(e4);
    if (!(e4>0)) {
        alert("结束序号只能是大于零的整数");
        return;
    }
}
if ((e3>0)&amp;&amp;(e4<=e3)) {
    alert("结束序号必须大于起始序号");
    return;
}

//发送
eio.appendInt32(g5.pageSize);
eio.appendInt32(g5.pageCurrent);
eio.appendInt32(e1);
eio.appendString16(e2);
eio.appendInt32(e3);
eio.appendInt32(e4);

taskSetItem.script2:
var pageSize = eio.readInt32();
var pageCurrent = eio.readInt32();
var e1 = eio.readInt32();
var e2 = eio.readString16();
var e3 = eio.readInt32();
var e4 = eio.readInt32();

//产生条件语句
var condition = "where accountName='" + db.encode(session.get("username")) + "'";
if (e1>0) {
    condition += " and batchId = " + e1;
}
if (e2!="") {
    condition += " and Note like '%" + db.encode(e2) + "%'";
}
if (e3>0) {
    condition += " and snStart<" + e4 + " and snEnd>" + e3;
}
var sql, sqlResult;

//获取记录数量
sql = "select count(*) as c from sourceInfo " + condition;
sqlResult = db.executeQuery("QR", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

//读取表
sql =
    "SELECT id, batchId, convert(varchar, regdatetime, 120) as regdatetime, " +
    "cast(snStart as varchar) +'-'+ cast(snEnd as varchar) as snRange, " +
    "snEnd - snStart as c, Note from sourceInfo " +
    condition + " order by snStart desc, regdatetime desc";
var sqlResult = db.executeQuery("QR", sql);
es.logWarning(sqlResult);
eio.appendInt32(count);
eio.appendDataSet(sqlResult, "id,batchId,regdatetime,snRange,c,Note", pageSize*(pageCurrent-1), pageSize);

taskSetItem.script3:
g5.pageRecordCount = eio.readInt32();
g5.dataset1 = eio.readDataSet();
g5.pageCount = parseInt((g5.pageRecordCount-1)/g5.pageSize)+1;
ee.setTableData(ee.getControl("tablebody1"), g5.dataset1, 0);
ee.setText(ee.getControl("layoutxitem11"), g5.pageCurrent + "/" + g5.pageCount);

