taskSetItem.script2:
var batchId = eio.readInt32();
var snStart = eio.readInt32();
var snEnd = eio.readInt32();
var note = eio.readString16();

var sql, sqlResult;

//检测批号和序号范围是否存在
sql = 
    "select count(*) as c from qrBatchInfo where accountName='" + db.encode(session.get("username")) + "' "+
    " and batchId=" + batchId +
    " and qrSnStart<=" + snStart +
    " and qrSnStart+qrSnCount>=" + snEnd;
sqlResult = db.executeQuery("QR", sql);
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

if (count==0) {
    eio.appendString8("F");
    eio.appendString16("对不起，你输入的产品批号或者序号范围不存在，请通过批号查询功能确定您目前已经购买的二维码批次信息");
} else {
    sql = 
        "insert into sourceInfo (accountName,batchId,snStart,snEnd,Note) values (" +
        "'" + db.encode(session.get("username")) + "'," +
        batchId + "," +
        snStart + "," +
        snEnd + "," +
        "'" + db.encode(note) + "')";
    db.executeNoneQuery("QR", sql);
    eio.appendString8("T");
}

taskSetItem.script3:
