control.onControlLoad:
//鼠标点击行激活处理
g5.t1Id = null;
g5.table1Click = function(sender) {
    if (eb.isEmpty(g5.dataset1)) {
        g5.t1Id = null;
        return;
    }
    g5.t1Id = parseInt(g5.dataset1.getData(0, sender.rowIndex));
    g5.t1f1 = g5.dataset1.getData(1, sender.rowIndex);
    g5.t1f2 = g5.dataset1.getData(2, sender.rowIndex);
    g5.t1f3 = g5.dataset1.getData(3, sender.rowIndex);
    g5.t1f4 = g5.dataset1.getData(4, sender.rowIndex);

    ee.setText(ee.getControl("panel26"), g5.t1f1);
    ee.setText(ee.getControl("panel31"), g5.t1f2);
    ee.setText(ee.getControl("panel27"), g5.t1f3);
    ee.setText(ee.getControl("panel32"), g5.t1f4);
    ee.setText(ee.getControl("panel6"), "");
    g5.t3Id = null;
    ee.setText(ee.getControl("panel48"), "");
    ee.setText(ee.getControl("panel49"), "");
    ee.setText(ee.getControl("panel50"), "");
    ee.setText(ee.getControl("panel51"), "");    
    ee.setText(ee.getControl("panel8"), "点击选择到达城市");
}
ee.setText(ee.getControl("panel26"), "");
ee.setText(ee.getControl("panel31"), "");
ee.setText(ee.getControl("panel27"), "");
ee.setText(ee.getControl("panel32"), "");

//鼠标点击行激活处理
g5.t3Id = null;
g5.table3Click = function(sender) {
    if (eb.isEmpty(g5.dataset3)) {
        g5.t3Id = null;
        return;
    }
    g5.t3Id = parseInt(g5.dataset3.getData(0, sender.rowIndex));
    g5.t3f1 = g5.dataset3.getData(1, sender.rowIndex);
    g5.t3f2 = g5.dataset3.getData(2, sender.rowIndex);
    g5.t3f3 = g5.dataset3.getData(3, sender.rowIndex);
    g5.t3f4 = g5.dataset3.getData(4, sender.rowIndex);

    ee.setText(ee.getControl("panel48"), g5.t3f1);
    ee.setText(ee.getControl("panel49"), g5.t3f2);
    ee.setText(ee.getControl("panel50"), g5.t3f3);
    ee.setText(ee.getControl("panel51"), g5.t3f4);
    ee.setText(ee.getControl("panel8"), "");
