control.onUpdate:
var cell;
if ((eb.isEmpty(g5.dataset2)) || (g5.dataset2.getData(0, sender.rowIndex).length==0)) {
    for (var i = 0; i < sender.parentNode.colCount; i++) {
        cell = ee.getTableCell(sender.parentNode, i, sender.rowIndex);
        ee.setText(cell, "");
    }
    return;
}
//航班
cell = ee.getTableCell(sender.parentNode, 0, sender.rowIndex);
ee.setText(cell, g5.dataset2.getData(1, sender.rowIndex));
//出发
cell = ee.getTableCell(sender.parentNode, 1, sender.rowIndex);
ee.setText(cell,
    g5.dataset2.getData(2, sender.rowIndex).substr(5,2) + "月" +
    g5.dataset2.getData(2, sender.rowIndex).substr(8,2) + "日<br/>" +
    g5.dataset2.getData(2, sender.rowIndex).substr(11,2) + "点" +
    g5.dataset2.getData(2, sender.rowIndex).substr(14,2) + "分"
    );
//到达
