ee.setText(cell,
    g5.dataset2.getData(3, sender.rowIndex).substr(5,2) + "月" +
    g5.dataset2.getData(3, sender.rowIndex).substr(8,2) + "日<br/>" +
    g5.dataset2.getData(3, sender.rowIndex).substr(11,2) + "点" +
    g5.dataset2.getData(3, sender.rowIndex).substr(14,2) + "分"
    );
//价格
