taskSetItem.script2:
var sql,sqlResult;
sql= 
    "SELECT   dbo.ticketInfo.codeDeparture, dbo.airport.cityCn, dbo.airport.cityEn, dbo.airport.nameCn, dbo.airport.nameEn " +
    "FROM      dbo.ticketInfo INNER JOIN " +
    "                dbo.airport ON dbo.ticketInfo.codeDeparture = dbo.airport.id " +
    "WHERE   (dbo.ticketInfo.status = 'IDL') " +
    "GROUP BY dbo.ticketInfo.codeDeparture, dbo.airport.cityCn, dbo.airport.cityEn, dbo.airport.nameCn, dbo.airport.nameEn " +
    "";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"codeDeparture,cityCn,cityEn,nameCn,nameEn", 0, 10000);

