taskSetItem.script2:
var id = eio.readInt32();

var sql,sqlResult;
sql= 
    "SELECT   dbo.airport.cityCn, dbo.airport.cityEn, dbo.airport.nameCn, dbo.airport.nameEn, dbo.ticketInfo.codeDestination " +
    "FROM      dbo.ticketInfo INNER JOIN " +
    "                dbo.airport ON dbo.ticketInfo.codeDestination = dbo.airport.id " +
    "WHERE   (dbo.ticketInfo.status = 'IDL') AND (dbo.ticketInfo.codeDeparture = " + id + ") " +
    "GROUP BY dbo.airport.cityCn, dbo.airport.cityEn, dbo.airport.nameCn, dbo.airport.nameEn, dbo.ticketInfo.codeDestination ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"codeDestination,cityCn,cityEn,nameCn,nameEn", 0, 10000);

