taskSetItem.script2:
var id1 = eio.readInt32();
var id2 = eio.readInt32();

var sql,sqlResult;
sql= 
    "SELECT *, CONVERT(varchar(100), timeDeparture, 20) as t1 " +
    "  ,CONVERT(varchar(100), timeArrival, 20) as t2 from ticketInfo " + 
    "WHERE   (dbo.ticketInfo.status = 'IDL') " +
    "    AND (dbo.ticketInfo.codeDeparture = " + id1 + ") " +
    "    AND (dbo.ticketInfo.codeDestination = " + id2 + ") ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
