control.onControlLoad:
setInterval(function(){
    document.getElementById("ee_edit1").childNodes[10].focus();
},500);
//document.getElementById("ee_edit1").childNodes[10].focus(); 

document.onkeydown=function(event){
        var e = event || window.event || arguments.callee.caller.arguments[0];
        if(e && e.keyCode==13){                   // enter 键
             scan();
        };  
        if(e && e.keyCode==8 || e && e.keyCode==46){ 
             Init();
             ee.setText(ee.getControl("panel32"),"订单提示：");              
        };
        if(e && e.keyCode==32){                       
            var R = document.getElementById("ee_panel27");  
            var C = document.getElementById("ee_panel20");
            if(R.style.visibility=="inherit"){
                insert();
                //ee.setVisible(ee.getControl("panel21"),false);
                ee.setVisible(ee.getControl("panel20"),false);
                ee.setVisible(ee.getControl("panel27"),false);
            };
            if(C.style.visibility=="inherit"){
                update();  
                //ee.setVisible(ee.getControl("panel21"),false);
                ee.setVisible(ee.getControl("panel20"),false);
                ee.setVisible(ee.getControl("panel27"),false);
            }
            // if(ee.getVisible(ee.getControl("panel27"))){} visibility:hidden;转换visibility: inherit
        };  
};
//entrance  入场
//appear 出场

