sqlResult=db.executeQuery("UJ",sql);
db.read(sqlResult);
var orderid = db.getString(sqlResult,"orderid");             
sql="select * from ujia_order where orderid ="+db.encode(orderid);//获取订单信息
sqlResult = db.executeQuery("UJ",sql);
db.read(sqlResult);  
var end_time = db.getString(sqlResult,"end_time");
var start_time = db.getString(sqlResult,"start_time"); 
sql="select * from ujia_scan where barcode ="+db.encode(edit);
sqlResult = db.executeQuery("UJ",sql);       
db.read(sqlResult);
var in_time = db.getString(sqlResult,"in_time");
var out_time = db.getString(sqlResult,"out_time");

var yutime = end_time - start_time;
var shitime = out_time - in_time;

var chaotime = shitime - yutime - 300;

var chaotime = parseInt(chaotime / 60);
var addMony = chaotime*10;

sql = "update ujia_scan set overtime_money ='"+
        addMony+"' where barcode='"+db.encode(edit)+"'";
sqlResult = db.executeNoneQuery("UJ",sql);
if(sqlResult == null){
    es.logWarning("信息修改失败,暂时不能出场，sql="+sql);  
