g5.init = function(){
    //还原 编辑或添加 层
    ee.setEditText(ee.getControl("edit1"),"");   
    ee.setEditText(ee.getControl("edit2"),"");  
    ee.setEditText(ee.getControl("edit3"),"");                     
    var img_name = "xuanze.png";
    ee.setImage(ee.getControl("panel37"),"/Ujia/userInfo/"+img_name,"fill","",""); 
    ee.setText(ee.getControl("panel22"),"编辑使用人");
}

control.onClick:
var url = window.location.search;
var index = url.indexOf("back_url="); 
var back_url = "";

if (index != -1){
    back_url = url.substring(index + 9);
    back_url =unescape(back_url);     
}
if (back_url != ""){
    window.location.href = ee.pageRoot + back_url;
}else {
    window.location.href = ee.pageRoot + "/usercenter";
}

g5.type_show = "add";   
g5.is_default_m = "1"; 
ee.setText(ee.getControl("panel22"),"新建使用人");
ee.setVisible(ee.getControl("panel21"),true);

control.onUpdate:
var str = text; 
var cell = sender.eeChildren[0]; 
var cell_top = cell.eeChildren[0];
var cell_bottom = cell.eeChildren[1]; 

cell.style.background = "white";

var arr = str.split("`");
var name = arr[0];
var tel = arr[1];
var idCard = arr[2];
var is_default = arr[3];  
var name_str = name+"      手机号："+tel;
var idCard_str = "身份证号：" + idCard;   

//cell_bottom 中处理
var cell_bottom_t = cell_bottom.eeChildren[0];
var cell_bottom_b = cell_bottom.eeChildren[1];
ee.setText(cell_bottom_t,name_str );
ee.setText(cell_bottom_b,idCard_str);    

//cell_top  中处理   
var cell_top_isDefault = cell_top.eeChildren[0];  
var img_name = "";  
var img_right_text = "";
if (is_default == 0){
    img_name = "yuan2.png"; 
    img_right_text = "默认使用人";
}else if (is_default == 1){
    img_name = "xuanze.png";  
    img_right_text = "设为默认使用人";
}else {
    console.log("默认图片状态出错");
}
ee.setImage(cell_top_isDefault,"/Ujia/userInfo/"+img_name,"fill","","");
ee.setText(cell_top.eeChildren[1],img_right_text);

control.onClick:
var text = ee.getText(this.parentNode.eeChildren[1]);
//console.log(text);
if (text == "设为默认使用人"){
    var index = (this.parentNode.parentNode.parentNode).rowIndex;     
    g5.choose_id = ee.getText(ee.getControl("tablebody1").cols[0].rows[index]);
    changeDefault();
}else {
    return;
}

