sql = "select id, concat_ws('`',name,tel,idcard,is_default) as data from ujia_user_infomation where uid = '"+id+"' order by id desc";  

//es.logWarning(sql);   


sqlResult = db.executeQuery("UJ",sql);   


eio.appendString8("T");
eio.appendDataSet(sqlResult,"id,data,data",0,1000);

