taskSetItem.script1:
var tt = "";
var ts = "";
if(g5.floatframe == 1){
    clearInterval(ts); 
    tt =  setInterval(show,10);
    //ee.getControl("panel9").style.left = "-130px";   
    function show(){   
        var animation = parseInt(ee.getControl("panel9").style.left);
        if(animation > -130){
            animation-=13;
            ee.getControl("panel9").style.left = animation+"px";
        }else {
            ee.getControl("panel9").style.left = "-130px"; 
            clearInterval(tt);
        }   
    }
    g5.floatframe = 2;
}else {
    clearInterval(tt);
    ts =  setInterval(show,10);
    //ee.getControl("panel9").style.left = "-130px";   
    function show(){   
        var animation = parseInt(ee.getControl("panel9").style.left);
        if(animation < 0){
            animation+=13;
            ee.getControl("panel9").style.left = animation+"px";
        }else {
            ee.getControl("panel9").style.left = "0px"; 
            clearInterval(ts);
        }   
    }
    //ee.getControl("panel9").style.left = "0px";
    g5.floatframe = 1
}
