control.onPageCreate:
//获取insert_id 和　back＿url
var url = window.location.href;
var index = url.indexOf("=/");
url = url.substring(index + 1);
var arr = url.split("red_url");     

var url_orig = arr[0];  // "/Ujia/pay&insert_id=1430&back_url=/Ujia/shoplist%26id%3D2170%26token%3Ddemo%26"
var url_red = arr[1];   // "%3D/simulation/path%23id%3D13%23token%3Ddemo"

var arr_orig = url_orig.split("&back_url=");
var url_use = arr_orig[0];
var url_back = arr_orig[1];  

var index2 = url_use.indexOf("insert_id=");
var insert_id = url_use.substring(index2 + 10);
//console.log(insert_id);
url_back = unescape(url_back);   
var index3 = url_back.indexOf("/shoplist");
url_back = url_back.substring(index3);
//console.log(url_back);           

url_red = unescape(url_red); 
var back_url = url_back + "red_url" + url_red; 
//console.log(back_url);

g5.insert_id = insert_id;
g5.back_url = back_url;           

eb.setCookie("insert_id",insert_id);
eb.setCookie("back_url",back_url);           


window.location.href="https://open.weixin.qq.com/connect/oauth2/authorize?appid=wxeeb9b2f6e1f01d23&response_type=code&redirect_uri=http%3A%2F%2Fm.1peijia.com%2FEEngine%2FujiaPay.htm&scope=snsapi_base&state=STATE#wechat_redirect";
