taskSetItem.script3:
//insert();
var timestamp = Date.parse(new Date());
var time0 = timestamp / 1000;
var intimes = eio.readInt32();
//console.log(time0);
if(time0 < intimes){
    var time1 = intimes - time0; 
    //console.log(time1);  

    if (time1 < 60){  

        ee.setText(ee.getControl("panel31"),"离开场还有"+time1+"秒");
    }
    else if(time1 < 3600){                            
        var M = parseInt(time1/60);
        ee.setText(ee.getControl("panel31"),"离开场还有"+M+"分");
    }else if(time1 < 86400){
        var H = parseInt(time1/3600);
        ee.setText(ee.getControl("panel31"),"离开场还有"+H+"小时");
    }else if(time1 < 2592000){
        var D = parseInt(time1/86400);
        ee.setText(ee.getControl("panel31"),"离开场还有"+D+"天");
    }else {
        ee.setText(ee.getControl("panel31"),"离开场还有一年以上");
    }       

    //ee.setText(ee.getControl("panel27"),"离开场还有"+D+"天"+H+"小时"+M+"分");

};
if(time0 >= intimes){
    var time1 = time0 - intimes;
    if (time1 < 60){  

        ee.setText(ee.getControl("panel31"),"开场已经"+time1+"秒");
    }
    else if(time1 < 3600){                            
        var M = parseInt(time1/60);
        ee.setText(ee.getControl("panel31"),"开场已经"+M+"分");
    }else if(time1 < 86400){
        var H = parseInt(time1/3600);
        ee.setText(ee.getControl("panel31"),"开场已经"+H+"小时");
    }else {
        ee.setText(ee.getControl("panel31"),"您的订单已过期");
    }
}

