g5.mingyuanKey = "<% request.get("mk"); %>";
g5.ncKey = "<% request.get("ncKey"); %>";

control.onControlLoad:
g5.refreshPage = function (index) {
    g5.pageCount[index] = parseInt(g5.dataset[index].rowCount / g5.pageSize) + 1;
    ee.setText(g5.pageLabel[index], g5.pageIndex[index] + "/" + g5.pageCount[index]);
}
//初始化报表统计表
var tableBody = ee.getControl("tablebody4");
for (var rowIndex = 0; rowIndex < tableBody.rowCount; rowIndex++) {
    for (var colIndex = 0; colIndex <= tableBody.colCount; colIndex++) {
        if ((colIndex ==1)&amp;&amp;(rowIndex==0)) {
            ee.setTableCellText(tableBody, colIndex, rowIndex, "数据分析-- 合约管控`http://218.75.64.147:9001/WebReport/ReportServer?reportlet=reportebs.cpt&amp;op=view");
        } else if ((colIndex ==2)&amp;&amp;(rowIndex==0)) {
            ee.setTableCellText(tableBody, colIndex, rowIndex, "04-15 10:00");
        } else {
            ee.setTableCellText(tableBody, colIndex, rowIndex, "");
        }
    }
}
//修改<A>颜色
g5.addCss = function (cssText) {
    var head = document.head || document.getElementsByTagName("head")[0];
    var style = document.createElement("style");
    style.type = "text/css";
    if (style.styleSheet) {
        style.styleSheet.cssText = cssText;
    } else {
        style.appendChild(document.createTextNode(cssText));
    }
    head.appendChild(style);
}
g5.addCss("a:link {color:#000000};");
g5.addCss("a:visited {color:#808080};");
g5.addCss("a:hover {color:#008000};");
