control.onControlLoad:
g5.show = function(c) {
    var cc = c.split(",");
    g5.showControls = cc;
    for (var i=0; i<cc.length; i++) {
        ee.setVisible(ee.getControl(cc[i]), false);
        ee.showAni(ee.getControl(cc[i]));
    }
}
g5.showClose = function() {
    var cc = g5.showControls;
    for (var i=0; i<cc.length; i++) {
        ee.getControl(cc[i]).style.visibility = "inherit";
    }
}

g5.showImage = function (img) {
    ee.setImage(ee.getControl("panel49"), ee.pageUrl + "/" + img, "fill", "", ""); 
    g5.show("panel45,panel47,panel48,panel49,panel51");
}
